import React, {useState} from 'react';
import { Typography } from '@material-ui/core';
import { Button } from '@material-ui/core';
import {Grid} from '@material-ui/core';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';

const Item = (props) => {
    const [toggle, setToggle] = useState(false);
    const [space, setSpace] = useState('30px');
    return (<Grid container
            direction="row"
            justify="center"
            alignItems="center"
            style={{color:'black', marginTop:'5px'}}>

                <Grid item xs={12}
                style={{color:'black'}}>
                    <Typography
                    display="block"
                    justifyContent="center"
                    align="center"
                    variant="h6"
                    style={{opacity:'100%', fontWeight:'bold', marginBottom:'0'}}>
                        {props.jobTitle}
                    </Typography>
                </Grid>

                <Grid item xs={12}>
                    <hr style={{border: '1px solid black', opacity:'50%', width:'50%'}}></hr>
                </Grid>

                <Grid item xs={12}>
                    <Typography
                        display="block"
                        justifyContent="center"
                        align="center"
                        variant="body1"
                        style={{opacity:'100%', marginBottom:'0'}}>
                            {props.company}
                        </Typography>
                </Grid>

                <Grid item xs={12}>
                    <Button color='inherit' variant='outlined'
                    style={{border:'transparent'}}
                    onClick={()=>{
                        setToggle(!toggle)
                        if (toggle) {
                            setSpace('30px')
                        }
                        else {
                            setSpace('5px')
                        }
                    }}><ExpandMoreIcon/></Button>
                </Grid>

                <Grid item xs={12}>
                    <Typography
                        display="block"
                        justifyContent="center"
                        align="center"
                        variant="body1"
                        style={{opacity:'100%', marginBottom:'0'}}
                        className={`paragraph ${toggle && 'active'}`}>
                            {props.text}
                        </Typography>
                </Grid>

            </Grid>

    )
}

export default Item;
