import React from 'react';
import { Typography } from '@material-ui/core';
import { Button } from '@material-ui/core';
import {Grid} from '@material-ui/core';
import GitHubIcon from '@material-ui/icons/GitHub';
import Project from './Project.js';
import MobileStepper from '@material-ui/core/MobileStepper';
import Paper from '@material-ui/core/Paper';
import KeyboardArrowLeft from '@material-ui/icons/KeyboardArrowLeft';
import KeyboardArrowRight from '@material-ui/icons/KeyboardArrowRight';
import SwipeableViews from 'react-swipeable-views';
import { autoPlay } from 'react-swipeable-views-utils';

const tutorialSteps = [
    {
      project: 'San Francisco – Oakland Bay Bridge, United States',
      company:
        'company',
    },
    {
    project: 'San Francisco – Oakland Bay Bridge, United States',
    company:
        'company',
    },
    {
    project: 'San Francisco – Oakland Bay Bridge, United States',
    company:
        'company',
    },
    {
    project: 'San Francisco – Oakland Bay Bridge, United States',
    company:
        'company',
    },
  ];

const Projects = () => {
    const [activeStep, setActiveStep] = React.useState(0);
    const maxSteps = tutorialSteps.length;

    const handleNext = () => {
        setActiveStep((prevActiveStep) => prevActiveStep + 1);
    };

    const handleBack = () => {
        setActiveStep((prevActiveStep) => prevActiveStep - 1);
    };

    const handleStepChange = (step) => {
        setActiveStep(step);
    };

    return (
        <Grid container
        direction="row"
        justify="center"
        alignItems="center"
        style={{marginLeft:'30px', paddingRight:'30px'}}>

            <Grid item xs={6}>
            <Typography
                display="block"
                justifyContent="center"
                align="center"
                variant="h3"
                style={{opacity:'100%', fontWeight:'bold', fontSize:'40px', color:'black',
                 backgroundColor:'rgba(205, 255, 219,0.8)', height:'200px', paddingTop:'25%'}}>
                    Projects
                </Typography>
            </Grid>

            <Grid item xs={6}>
                <div>
                    <Paper square elevation={0} className={classes.header}>
                        <Typography>{tutorialSteps[activeStep].label}</Typography>
                    </Paper>
                    <AutoPlaySwipeableViews
                        axis={theme.direction === 'rtl' ? 'x-reverse' : 'x'}
                        index={activeStep}
                        onChangeIndex={handleStepChange}
                        enableMouseEvents
                    >
                        {tutorialSteps.map((step, index) => (
                        <div key={step.label}>
                            {Math.abs(activeStep - index) <= 2 ? (<>
                            <h1>{step.project}</h1>
                            <p>{step.company}</p>
                            <GitHubIcon />
                            </>) : null}
                        </div>
                        ))}
                    </AutoPlaySwipeableViews>
                    <MobileStepper
                        steps={maxSteps}
                        position="static"
                        variant="text"
                        activeStep={activeStep}
                        nextButton={
                        <Button size="small" onClick={handleNext} disabled={activeStep === maxSteps - 1}>
                            Next
                            {theme.direction === 'rtl' ? <KeyboardArrowLeft /> : <KeyboardArrowRight />}
                        </Button>
                        }
                        backButton={
                        <Button size="small" onClick={handleBack} disabled={activeStep === 0}>
                            {theme.direction === 'rtl' ? <KeyboardArrowRight /> : <KeyboardArrowLeft />}
                            Back
                        </Button>
                        }
                    />
                </div>
            </Grid>

        </Grid>

    )
}

export default Projects;
